import sys,json,re

# Get the script"s input
relationships = sys.argv[1]
observables = sys.argv[2]
targets = sys.argv[3]

# Parse the relationships (or default to an empty list)
if len(relationships) > 0:
	relationships = json.loads(relationships)
else:
	relationships = []

# Parse the observables (or default to an empty list)
if len(observables) > 0:
	observables = json.loads(observables)
else:
	observables = []

# Parse the targets (or default to an empty list)
if len(targets) > 0:
	targets = json.loads(targets)
else:
	targets = []

# Placeholders
observablesOut = []
internalIp = ""
resolvedDomainsOut = []

# Appends an observable to our master list if it"s not an RFC 1918 address
def appendObservable(observable):
	if observable["type"] != "ip" or (observable["type"] == "ip" and not re.match(r'(^127\.)|(^10\.)|(^172\.1[6-9]\.)|(^172\.2[0-9]\.)|(^172\.3[0-1]\.)|(^192\.168\.)', observable["value"])):
		if observable not in observablesOut:
			observablesOut.append( { "type" : observable["type"], "value" : observable["value"] })

# For each relationship...
for relation in relationships:
	# Append its observables
	appendObservable(relation["source"])
	appendObservable(relation["related"])

	# Is this a domain resolution?
	if relation["relation"] == "Resolved_To" and relation["related"]["type"] == "domain":
		domainObject = { "domain": relation["related"]["value"] }
		if domainObject not in resolvedDomainsOut:
			resolvedDomainsOut.append(domainObject)

# For each observable...
for observable in observables:
	appendObservable(observable)

# For each target...
for target in targets:
	# Check its observables
	for observable in target["observables"]:
		appendObservable(observable)

		# Is this an internal IP?
		if observable["type"] == "ip" and re.match(r'(^127\.)|(^10\.)|(^172\.1[6-9]\.)|(^172\.2[0-9]\.)|(^172\.3[0-1]\.)|(^192\.168\.)', observable["value"]):
			internalIp = observable["value"]

# Format the output JSON
observablesOut = json.dumps(observablesOut)
resolvedDomainsOut = json.dumps(resolvedDomainsOut)

import sys,json

# Get the module information
ampModule = json.loads(sys.argv[1])

# Placeholders
fileHashesOut = []

# For each sighting...
for sighting in ampModule["data"]["sightings"]["docs"]:
	# For each relationship...
	for relation in sighting["relations"]:
		sourceHash = { "hash": relation["source"]["value"] }
		if relation["source"]["type"] == "sha256" and sourceHash not in fileHashesOut:
			fileHashesOut.append(sourceHash)

		relatedHash = { "hash": relation["related"]["value"] }
		if relation["related"]["type"] == "sha256" and relatedHash not in fileHashesOut:
			fileHashesOut.append(relatedHash)

fileHashesOut = json.dumps(fileHashesOut)
